def eval_with_cross_val(model, X, y, num_splits, weight_classes=False):
    scores = []
    kappa_scores = []
    scores_weighted = []
    kappa_scores_weighted = []
    cor_scores = []
    cv = KFold(n_splits=num_splits, shuffle=True)
    if weight_classes:
        print("STARTING-WITH-WEIGHT-CLASSES")
    else:
        print("STARTING")

    for train_index, test_index in cv.split(X):
        X_train, X_test = X[train_index], X[test_index]
        y_train, y_test = y[train_index], y[test_index]
        # print("train index", train_index)
        # print("test index", test_index)
        # print("x_train_shape", X_train.shape, "X_test shape", X_test.shape)
        if not weight_classes:
            model = model.fit(X_train, y_train)
        else:
            weights_train = compute_sample_weight("balanced", y_train, indices=None)
            model = model.fit(X_train, y_train, sample_weight=weights_train)

        weights_test = compute_sample_weight("balanced", y_test, indices=None)
        score_weighted = model.score(X_test, y_test, sample_weight=weights_test)
        kappa_score_weighted = cohen_kappa_score(y_test, model.predict(X_test), sample_weight=weights_test)

        score = model.score(X_test, y_test)
        kappa_score = cohen_kappa_score(y_test, model.predict(X_test))
        cor_scores.append(np.average(np.corrcoef(model.predict(X_test))))
        scores.append(score)
        kappa_scores.append(kappa_score)
        scores_weighted.append(score_weighted)
        kappa_scores_weighted.append(kappa_score_weighted)

    print("scores", scores)
    print("kappa scores", kappa_scores)
    print("avg scores", sum(scores) / len(scores))
    print("avg kappa scores", sum(kappa_scores) / len(kappa_scores))

    print("scores weighted", scores_weighted)
    print("kappa scores weighted", kappa_scores_weighted)

    print("avg scores weighted", sum(scores_weighted) / len(scores_weighted))
    print("avg kappa scores weighted", sum(kappa_scores_weighted) / len(kappa_scores_weighted))

    print("cor scores", cor_scores)
    print("cor scores", sum(cor_scores)/len(cor_scores))
    print("FINISHED")


