    model_LinearSVC = svm.LinearSVC()

    model_SVC = svm.SVC(probability=False)  # True slows down process
    model_DecisionTreeClassifier = tree.DecisionTreeClassifier()
    model_LogisticRegression = LogisticRegression()
    model_MLP = MLPClassifier()

    models = [model_LogisticRegression, model_MLP, model_SVC, model_MultinomialNB, model_DecisionTreeClassifier]

    for model in models:
        print("*" * 100)
        print(model)
        eval_with_cross_val(model, X, y, num_splits=5)
        print("-" * 100)
        eval_with_cross_val(model, X, y, num_splits=5, weight_classes=True)
